import React from 'react';
import { Bar } from 'react-chartjs-2';

const Result = () => {
  const data = {
    labels: ["DesignThinking", "Java", "AIML", "CloudComputing"],
    datasets: [
      {
        label: "Total Count/Value",
        data: [1552, 1319, 613, 1400],
        backgroundColor: ["teal", "aqua", "grey", "black"],
        borderColor: ["aqua", "green", "red", "yellow"],
        borderWidth: 0.5,
      },
    ],
  };

  const options = {
    maintainAspectRatio: false,
    scales: {
      x: {
        type: 'category',
        stacked: true,
      },
      y: {
        stacked: true,
        beginAtZero: true,
      },
    },
    legend: {
      labels: {
        fontSize: 15,
      },
    },
  };

  return (
    <div>
      <h2>Bar Graph Example</h2>
      <Bar data={data} options={options} width={400} height={200} />
    </div>
  );
};

export default Result;
