import React, { useState } from 'react'
import "../styles/letter.css";
const Letter = () => {
    const [name,setname]=useState("");
    const [reg,setreg]=useState("");
    const [sub,setsub]=useState("");
    const [data,setdate]=useState("");
    const [body,setbody]=useState("");
    const handleSubmit = async e => {
        e.preventDefault();
        const currentDate = new Date().toISOString().slice(0, 10);
        setdate(currentDate);
        try {
          await axios.post('/insert_letter', {date:date, sname:name,regno: reg,sub: sub,body: body,status:"Pending"});
          alert('Data added successfully!');
        } catch (error) {
          console.error('Error adding data:', error);
          alert('An error occurred while adding data.');
        }
      };
  return (
    <div class="letter">
        <section id="form-section">
        <h1 class="h1-tag">Write a Letter...</h1>
        <form class="form-content-section">
            <div class="form-group">
                <label for="exampleInputText" style={{fontSize:20}}>𝑵𝒂𝒎𝒆</label>
                <input type="text" class="form-control" value={name} onChange={(ev)=>setname(ev.target.value)} id="exampleInputText" placeholder="Enter Your Name" />
            </div>
            <br/>
            <div class="form-group">
                <label for="exampleInputEmail1" style={{fontSize:20}}>𝑹𝒆𝒈𝒊𝒔𝒕𝒆𝒓𝑵𝒖𝒎𝒃𝒆𝒓</label>
                <input type="email" class="form-control" value={reg} onChange={(ev)=>setreg(ev.target.value)}
id="exampleInputEmail1" aria-describedby="emailHelp"
                    placeholder="Enter email"/>
               
            </div>
            <br/>
            <div class="form-group">
                <label for="exampleInputText" style={{fontSize:20}}>𝑺𝒖𝒃𝒋𝒆𝒄𝒕
</label>
                <input type="text" class="form-control" value={sub} onChange={(ev)=>setsub(ev.target.value)}
id="exampleInputText"/>
            </div>
            <br/>
            <div class="form-group">
                <label for="exampleFormControlTextarea1" style={{fontSize:20}}>𝑩𝒐𝒅𝒚
</label>
                <textarea class="form-control" value={body} onChange={(ev)=>setbody(ev.target.value)}
id="exampleFormControlTextarea1" rows="3"></textarea>
            </div><br/>
            <div>
                
            </div>
            <button type="submit" class="btn btn-primary myclass">Submit</button>
        </form>
        <br/><br/><br/>
        <div ><h2 style={{marginLeft:300}}>𝑴𝒚 𝑨𝒑𝒑𝒍𝒊𝒄𝒂𝒕𝒊𝒐𝒏𝒔</h2><br/>
            <table class="table table-bordered">
    <thead>
      <tr>
        <th>Date</th>
        <th>Subject</th>
        <th>Status</th>
      </tr>
    </thead>
    <tbody>
      <tr>
        <td>01-02-2023</td>
        <td>Fever</td>
        <td ><center ><b style={{backgroundColor:'#fc7968',padding:5,borderRadius:5}}>Rejected</b></center></td>
      </tr>
      <tr>
        <td>01-02-2024</td>
        <td>Smart India Hackathon</td>
        <td ><center ><b style={{backgroundColor:'#67fa3e',padding:5,borderRadius:5}}>Accepted</b></center></td>
      </tr>
      <tr>
        <td>July</td>
        <td>Dooley</td>
        <td ><center ><b style={{backgroundColor:'#ecfa2a',padding:5,borderRadius:5}}>Pending</b></center></td>
      </tr>
    </tbody>
  </table></div>
        
    </section>
    
    </div>
  )
}
export default Letter;
